insert into book_info values(59, 2, '59.jpg', '黄金时代', '王小波', 34.30, 35.00, 1, now(), now());
insert into book_detail values(59, 59, '★王小波代表作！获第13届台湾联合报文学奖中篇小说大奖，入选《亚洲周刊》 二十世纪中文小说一百强 。特别收入珍贵手稿！ ★自由理性、特立独行、黑色幽默，二十年来王小波的文字像一面猎猎旌旗感召着一代代年轻人，王小波是书店里永不消失的风景！ ★王小波全部作品的独家授权！包括从未面市的遗稿及大量私人照片！ ★李银河老师，亲自校勘全稿，并作序追忆对王小波的思念之情。 ★王蒙、王朔、李敬泽、章诒和、徐则臣、徐浩峰、麦家、冯唐、柴静，诸多文坛名家倾情推荐，致敬王小波。', '北京十月文艺出版社', '2017-04-01', '', '', now(), now());
insert into book_sku values(59, 59, 1000, '', now(), now());
insert into book_info values(60, 2, '60.jpg', '冷场', '李诞', 42.00, 42.00, 1, now(), now());
insert into book_detail values(60, 60, '（温馨提示：敬爱的读者，由于预售量较大，小当会根据到货时间分批发货，请各位耐心等待）。 《脱口秀大会》主持人、网络人气吐槽大V李诞诚意新作，人间百态故事集。 韩寒监制，「ONE 一个」工作室出品。 一个平行宇宙中的李诞，嬉笑怒骂背后对人间的终极吐槽 摇摆不定的恋人、坚定信仰的窃贼，每个人都会从中找到自己 借字浇愁，犀利温柔。', '四川文艺出版社', '2018-11-01', '', '', now(), now());
insert into book_sku values(60, 60, 1000, '', now(), now());
